		if _, err := rand.Read(cgc.mask); err != nil {
			panic(err) // rand.Read should never fail on a healthy system.
		}

		// [ mask | masked_token ]
		copy(cgc.out, cgc.mask)

		// XOR hides the real token while remaining reversible
